import fetch from 'isomorphic-unfetch'
import { server } from '../config'

const Todos = ({ todos }) => {
  return (
    <div>
      <p>My Todos</p>
      <ul>{todos && todos.map((t, i) => <li key={i}>{t.text}</li>)}</ul>
    </div>
  )
}

Todos.getInitialProps = async ({ req }) => {
  /*   const protocol = req ? req.headers['x-forwarded-proto'] || 'http' : ''
