const Todos = todos => {
  return (
    <div>
      <p>My Todos</p>
      <ul>{todos && todos.map((t, i) => <li key={i}>t.text</li>)}</ul>
    </div>
  )
}

Todos.getInitialProps = async () => {
  const response = await fetch('http://localhost:3000/api/todos')
  const todos = await response.json()

  return { todos }
}

export default Todos
